        <label>Enter number of Blood Bags</label>
      </div>
      <a id="button1" href="#" onClick={this.usedButton2} ref="submit2" >
        Submit
      </a>
    </form>
  </div>
  <div class="existing-box">
    <h2>Existing Patient</h2>
