import HospitalHeader from "../../headers/hospital";
import {Component} from 'react'
import axios from 'axios'
import TrigerAxios from './Hospital-TrackingBlood'


export default class  hospitalTrackBloodBagInfo extends Component{
  constructor(props){
    super(props)
    this.state={
      bNumber: null,
    }
  }
  handleInputChange(value){
    this.setState({
      bNumber: value
    })
  }

  render(){
    return(
        <div>
            <HospitalHeader />
            <div class="login-box">
    <h2>Info Needed</h2>
    <form>
      <div class="user-box">
        <input type="text" value={this.state.bNumber} onChange={(e) =>{this.handleInputChange(e.target.value)}}/>
        <label>Enter Blood Bag ID</label>
      </div>
      <a id="button" href="#" onClick={TrigerAxios(this.state.bNumber)}>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        Submit
      </a>
    </form>
  </div>
        </div>

    );
}
}
