import HospitalHeader from '../../headers/hospital'
import { Component } from 'react';
import axios from 'axios';


export class TestFn extends Component{
    constructor(props){
      super(props);
      this.confirm = this.confirm.bind(this);
      this.state = {
        data: 'Confirm',
        isLoading: false,
      };
    }

    confirm(){
      axios.get('http://localhost:5000/query/bag?id=BD576:O-')
      .then(response =>{
        console.log(response);
        alert(response.data.output)
        this.setState({ data: response.data.output, isLoading: false });
      })
      .catch(error=>{
        this.setState({ data: error, isLoading: false });
      })
    }

    render(){
    return(
        <div>
      <HospitalHeader/>
      <div id="rec">
      <div class="login-box">
      <h2>Info Needed</h2>
      <form >
        <div className="user-box">
          <input type="text" name="" required=""/>
          <label>Enter Blood Bag ID</label>
        </div>
        <button id="confirm"  onClick={this.confirm} disabled={this.state.isLoading}>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          {this.state.data}
        </button>
      </form>
    </div>
    </div>
    </div>  
    );

  }
}
